import { Container, Row, Col, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../Standard-Component-style.css'
import './Modal.css'

export default function Modal_Component({active, setActive}) {
    const { t, i18n } = useTranslation("global");
    return(
        <div className={active ? "modal active" : "modal"} onClick={() => setActive(false)}>
            <div className='modal-content' onClick={e => e.stopPropagation()}>
                <div className='button-container'>
                    <Button className='close-button' onClick={() => setActive(false)}>X</Button>
                </div>
                <div className='main-modal-content'>
                    <div className='modal-content-header'>
                        <h1>{t("modal.modal-h1")}</h1>
                    </div>
                    <p>{t("modal.modal-p-1")}</p>
                    <p>{t("modal.modal-p-2")}</p>
                    <p>{t("modal.modal-form")}</p>
                </div>
            </div>
        </div>
    )
}
