import { useTranslation } from 'react-i18next';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../../Standard-Component-style.css'
import '../Book.css';

export default function TheIntermediateGeneration_Read_Component() {
    const { t, i18n } = useTranslation("global");

    const getBookPath = () => {
        if(i18n.language == 'en')
        {
            return `/public/materials/book/IntermediateGeneration/en/Intermediate Generation.pdf`;
        }
        else
        {
            return `/public/materials/book/IntermediateGeneration/ua/Проміжне покоління.pdf`;
        }
    };

    return (
        <div fluid className='reader-book fluid-fix mar-96'>
            {/* <embed src={getBookPath()} className="mar-96"></embed> */}
            <iframe src={getBookPath()} className="mar-96" width="100%" height="100%"></iframe>
        </div>
    );
}
